CREATE TABLE thinks(id INTEGER PRIMARY KEY AUTOINCREMENT, title VARCHAR,listIndex INTEGER,color INTEGER,createdAt DATETIME);CREATE TABLE annotations(id INTEGER PRIMARY KEY AUTOINCREMENT,think_id INTEGER NOT NULL,title VARCHAR,text TEXT,color INTEGER, listIndex INTEGER,createdAt DATETIME,FOREIGN KEY (think_id) REFERENCES thinks(id) ON DELETE CASCADE);CREATE TABLE annotation_files(id INTEGER PRIMARY KEY AUTOINCREMENT,annotation_id INTEGER NOT NULL,title VARCHAR,description TEXT,type VARCHAR,path TEXT NOT NULL,FOREIGN KEY (annotation_id) REFERENCES annotations(id) ON DELETE CASCADE)